"use client";

import {
  Combobox,
  ComboboxAnchor,
  ComboboxContent,
  ComboboxEmpty,
  ComboboxInput,
  ComboboxItem,
  ComboboxLabel,
  ComboboxTrigger,
} from "@/registry/default/ui/combobox";
import { ChevronDown } from "lucide-react";
import * as React from "react";

const frameworks = [
  { label: "Next.js", value: "nextjs" },
  { label: "SvelteKit", value: "sveltekit" },
  { label: "Nuxt", value: "nuxt" },
  { label: "Remix", value: "remix" },
  { label: "Astro", value: "astro" },
  { label: "SolidStart", value: "solidstart" },
  { label: "Qwik", value: "qwik" },
  { label: "Gatsby", value: "gatsby" },
];

export default function ComboboxCustomFilterDemo() {
  const [value, setValue] = React.useState("");

  // Custom filter that only matches from the start of the string
  function onFilter(options: string[], term: string) {
    return options.filter((option) =>
      option.toLowerCase().startsWith(term.toLowerCase()),
    );
  }

  return (
    <Combobox value={value} onValueChange={setValue} onFilter={onFilter}>
      <ComboboxLabel>Framework</ComboboxLabel>
      <ComboboxAnchor>
        <ComboboxInput placeholder="Select framework..." />
        <ComboboxTrigger>
          <ChevronDown className="h-4 w-4" />
        </ComboboxTrigger>
      </ComboboxAnchor>
      <ComboboxContent>
        <ComboboxEmpty>No framework found.</ComboboxEmpty>
        {frameworks.map((framework) => (
          <ComboboxItem key={framework.value} value={framework.value}>
            {framework.label}
          </ComboboxItem>
        ))}
      </ComboboxContent>
    </Combobox>
  );
}
