"use client";

import { Button } from "@/components/ui/button";
import * as Editable from "@/registry/default/ui/editable";
import * as React from "react";

export default function EditableDemo() {
  return (
    <Editable.Root placeholder="Click to edit">
      <Editable.Label>Basic Editable</Editable.Label>
      <Editable.Area>
        <Editable.Preview />
        <Editable.Input />
      </Editable.Area>
      <Editable.Trigger className="mt-2" asChild>
        <Button size="sm" className="w-fit">
          Edit
        </Button>
      </Editable.Trigger>
      <Editable.Toolbar className="mt-2">
        <Editable.Submit asChild>
          <Button size="sm">Save</Button>
        </Editable.Submit>
        <Editable.Cancel asChild>
          <Button variant="outline" size="sm">
            Cancel
          </Button>
        </Editable.Cancel>
      </Editable.Toolbar>
    </Editable.Root>
  );
}
