"use client";

import * as Editable from "@/registry/default/ui/editable";
import * as React from "react";

export default function EditableDoubleClickDemo() {
  return (
    <div className="flex flex-col gap-4">
      <Editable.Root defaultValue="Double click to edit" triggerMode="dblclick">
        <Editable.Label>Double Click Editable</Editable.Label>
        <Editable.Area>
          <Editable.Preview />
          <Editable.Input />
        </Editable.Area>
        <Editable.Toolbar>
          <Editable.Submit>Save</Editable.Submit>
          <Editable.Cancel>Cancel</Editable.Cancel>
        </Editable.Toolbar>
      </Editable.Root>
    </div>
  );
}
