"use client";

import * as Editable from "@/registry/default/ui/editable";
import * as React from "react";

export default function EditableReadonlyDemo() {
  return (
    <Editable.Root defaultValue="This content is read-only" readOnly>
      <Editable.Label>Read Only Editable</Editable.Label>
      <Editable.Area>
        <Editable.Preview />
        <Editable.Input />
      </Editable.Area>
      <Editable.Toolbar>
        <Editable.Submit>Save</Editable.Submit>
        <Editable.Cancel>Cancel</Editable.Cancel>
      </Editable.Toolbar>
    </Editable.Root>
  );
}
