import * as TagsInputPrimitive from "@diceui/tags-input";
import { X } from "lucide-react";

export default function TagsInputDemo() {
  return (
    <TagsInputPrimitive.Root className="flex w-[380px] flex-col gap-2">
      <TagsInputPrimitive.Label className="font-medium text-sm leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">
        Fruits
      </TagsInputPrimitive.Label>
      <TagsInputPrimitive.Content className="flex min-h-10 w-full flex-wrap items-center gap-1.5 rounded-md border border-input bg-background px-3 py-2 text-sm focus-within:ring-1 focus-within:ring-ring disabled:cursor-not-allowed disabled:opacity-50">
        {({ value }) => (
          <>
            {value.map((item) => (
              <TagsInputPrimitive.Item
                key={item}
                value={item}
                className="bg-blue-100 text-blue-800 rounded-md flex items-center gap-1 px-2 py-1 data-[selected]:ring-2 data-[highlighted]:ring-blue-500 data-[disabled]:opacity-50"
              >
                <TagsInputPrimitive.Text className="truncate" />
                <TagsInputPrimitive.Delete className="h-4 w-4 shrink-0 rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100">
                  <X className="size-3.5" />
                </TagsInputPrimitive.Delete>
              </TagsInputPrimitive.Item>
            ))}
            <TagsInputPrimitive.Input
              placeholder="Add fruit..."
              className="flex-1 bg-transparent outline-none placeholder:text-muted-foreground disabled:cursor-not-allowed disabled:opacity-50"
            />
          </>
        )}
      </TagsInputPrimitive.Content>
      <TagsInputPrimitive.Clear className="h-9 rounded-sm border border-input bg-background shadow-sm hover:bg-accent hover:text-accent-foreground">
        Clear
      </TagsInputPrimitive.Clear>
    </TagsInputPrimitive.Root>
  );
}
